mapper.select:
select

from tbl_order_deal
where 1=1

    and id = #{id,jdbcType=VARCHAR}


    and order_no = #{orderNo,jdbcType=VARCHAR}


    and body = #{body,jdbcType=VARCHAR}


    and detail = #{detail,jdbcType=VARCHAR}


    and number = #{number,jdbcType=VARCHAR}


    and price = #{price,jdbcType=VARCHAR}


    and total_fee = #{totalFee,jdbcType=VARCHAR}


    and spbill_create_ip = #{spbillCreateIp,jdbcType=VARCHAR}


    and pay_start_time = #{payStartTime,jdbcType=VARCHAR}


    and pay_end_time = #{payEndTime,jdbcType=VARCHAR}


    and goods_tag = #{goodsTag,jdbcType=VARCHAR}


    and openid = #{openid,jdbcType=VARCHAR}


    and pay_time = #{payTime,jdbcType=VARCHAR}


    and pay_status = #{payStatus,jdbcType=VARCHAR}


    and maniche_id = #{manicheId,jdbcType=VARCHAR}


    and area_id = #{areaId,jdbcType=VARCHAR}


    and university_id = #{universityId,jdbcType=VARCHAR}


    and create_time = #{createTime,jdbcType=VARCHAR}


    and create_user = #{createUser,jdbcType=VARCHAR}

